const test = require('tape')
const tangle = require('@tangle/test')

const Strategy = require('../')
const {
  identity,
  isConflict,
  isValidMerge,
  merge
} = Strategy()

const buildGraph = (mermaid) => tangle.buildGraph(mermaid, { dataField: 'title' })

test('merging', t => {
  // merging stuff! ///////////////////////////
  const tips = ['B', 'C']
  t.equal(isConflict(buildGraph(`
    A-->B
    A-->C
  `), tips, 'title'), false, 'no title set')

  t.equal(isConflict(buildGraph(`
    A[{ set: 'cat'}]-->B
    A-->C
  `), tips, 'title'), false, 'no conflict (two identity)')
  t.equal(isConflict(buildGraph(`
    A[{set: 'cat'}]-->B[{set: 'dog'}]
    A-->C
  `), tips, 'title'), false, 'no conflict (one identity)')
  t.equal(isConflict(buildGraph(`
    A[{set: 'cat'}]-->B[{set: 'dog'}]
    A-->C[{set: 'dog'}]
  `), tips, 'title'), false, 'no conflict (same transform)')
  t.equal(isConflict(buildGraph(`
    A[{set: 'cat'}]-->B[{set: 'dog'}]
    A-->C[{set: 'mouse'}]
  `), tips, 'title'), true, 'conflict')

  t.equal(isValidMerge(buildGraph(`
    A-->B-->D
    A-->C-->D
  `), 'D', 'title'), true, 'merge identities')
  t.equal(isValidMerge(buildGraph(`
    A-->B[{set: 'dog'}]-->D
    A-->C-->D
  `), 'D', 'title'), true, 'identity merge is fine for non-conflict')
  t.equal(isValidMerge(buildGraph(`
    A-->B[{set: 'dog'}]-->D
    A-->C[{set: 'cat'}]-->D
  `), 'D', 'title'), false, 'merge that does not resolve conflict is invalid')

  t.equal(isValidMerge(buildGraph(`
    A-->B[{set: 'dog'}]-->D[{set: 'mouse'}]
    A-->C[{set: 'cat'}]-->D
  `), 'D', 'title'), true, 'nonidentity merge node')

  t.deepEqual(merge(buildGraph(`
    A-->B-->D
    A-->C-->D
  `), 'D', 'title'), identity(), 'merge identities')
  t.deepEqual(merge(buildGraph(`
    A-->B[{set: 'dog'}]-->D
    A-->C-->D
  `), 'D', 'title'), { set: 'dog' }, 'identity merge is fine for non-conflict')
  t.throws(() => merge(buildGraph(`
    A-->B[{set: 'dog'}]-->D
    A-->C[{set: 'cat'}]-->D
  `), 'D', 'title'), /cannot merge invalid transformations/, 'merge that does not resolve conflict is invalid')
  t.deepEqual(merge(buildGraph(`
    A-->B[{set: 'dog'}]-->D[{set: 'mouse'}]
    A-->C[{set: 'cat'}]-->D
  `), 'D', 'title'), { set: 'mouse' }, 'nonidentity merge node')

  t.end()
})

test('merging (complex)', t => {
  // Two branches, setting in a chain
  t.deepEqual(merge(buildGraph(`
    A-->B------>M
    A-->C-->D-->M
        C-->E-->M
   `), 'M', 'title'), identity(), 'merge identities')
  t.deepEqual(merge(buildGraph(`
     A-->B-->M
     A[{set:'dog'}]-->C-->D-->M
                      C-->E-->M
   `), 'M', 'title'), { set: 'dog' }, 'only root has title')
  t.deepEqual(merge(buildGraph(`
     A-->B-->M
     A[{set:'dog'}]-->C[{set:'cat'}]-->D-->M
                      C-->E-->M
   `), 'M', 'title'), { set: 'cat' }, 'both branches have title')
  t.deepEqual(merge(buildGraph(`
     A-->B-->M
     A[{set:'dog'}]-->C[{set:'cat'}]-->D[{set:'mouse'}]-->M
                      C-->E-->M
   `), 'M', 'title'), { set: 'mouse' }, 'Tip overwrites earlier branches')
  t.throws(() => merge(buildGraph(`
    A-->B-->M
    A[{set:'dog'}]-->C[{set:'cat'}]-->D[{set:'mouse'}]-->M
                     C-->E[{set:'kiwi'}]-->M
   `), 'M', 'title'), /cannot merge invalid transformations/, 'any other node would conflict')

  // Two merge nodes E and M in sequence
  const twoMerge = buildGraph(`
   A-->B[{ set: 'cats' }]->D-->M
   A-->C[{ set: 'dogs' }]->F-->M
       B------------------>E-->M
       C------------------>E
    `)
  t.equal(isConflict(twoMerge, ['B', 'C'], 'title'), true, 'Conflict resolving E')
  t.equal(isValidMerge(twoMerge, 'E', 'title'), false, 'Conflict resolving E')
  t.throws(() => merge(twoMerge, 'E', 'title'), /cannot merge invalid transformations/, 'E is invalid merge')
  // t.throws(() => merge(twomerge, 'M', 'title'), /cannot merge invalid transformations/,
  // 'invalid merge earlier in graph is not caught')  //Actual result: {set:dogs}
  t.deepEqual(merge(buildGraph(`
  A-->B[{ set: 'cats' }]->D-->M
  A-->C[{ set: 'dogs' }]->F-->M
      B------------------>E-->M
      C------------------>E[{ set: 'either' }]
   `), 'E', 'title'), { set: 'either' }, 'earlier merge')
  t.deepEqual(merge(buildGraph(`
  A-->B[{ set: 'cats' }]->D-->M
  A-->C[{ set: 'dogs' }]->F-->M
      B------------------>E-->M
      C------------------>E[{ set: 'either' }]
   `), 'M', 'title'), { set: 'either' }, 'stacked merges')

  const middleX = buildGraph(`
  A-->B-->D[{ set: 'left' }]--->G
      B-->E-------------------->G-->I
  A-->C-->E-------------------->H-->I
      C-->F[{ set: 'right' }]-->H
   `)
  t.deepEqual(merge(middleX, 'G', 'title'), { set: 'left' }, 'left merge')
  t.deepEqual(merge(middleX, 'H', 'title'), { set: 'right' }, 'right merge')
  t.throws(() => merge(middleX, 'I', 'title'), /cannot merge invalid transformations/, 'left and right conflict')
  t.end()
})
